import React, { useState, useEffect } from 'react';

function Carousel() {
  const images = [
    "data:image/jpeg;base64,",
    "data:image/jpeg;base64,",
    "data:image/jpeg;base64,",
    "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcS4S82TIZ2UZmwjPhYeVBhX7h0sIAiY9yGL3g&s",
    "data:image/jpeg;base64,",
  ];

  const [currentIndex, setCurrentIndex] = useState(0);

  useEffect(() => {
    const interval = setInterval(() => {
      setCurrentIndex((prevIndex) => (prevIndex + 1) % images.length);
    }, 2000);
    return () => clearInterval(interval);
  }, [images.length]);

  return (
    <div className="w-full max-w-2xl mx-auto">
      <div className="aspect-video rounded-lg overflow-hidden shadow-md bg-gray-200">
        <img
          src={images[currentIndex]}
          alt={`Item ${currentIndex + 1}`}
          className="w-full h-full object-cover transition-all duration-500"
        />
      </div>
      <div className="flex justify-center mt-4 space-x-5">
        {images.map((_, index) => (
          <button
            key={index}
            className={`w-3 h-3 rounded-full transition-colors duration-300 ${index === currentIndex ? 'bg-green-600' : 'bg-gray-300'}`}
            onClick={() => setCurrentIndex(index)}
            aria-label={`Go to item ${index + 1}`}
          />
        ))}
      </div>
    </div>
  );
}

export default Carousel;
